def split_long_segments_for_padded_chunks(segments, max_total_duration, padding):
    """
    Splits segments that are too long to fit in a chunk, even by themselves,
    once padding is considered.
    
    Args:
        segments (list): The original list of segments.
        max_total_duration (float): The absolute maximum duration of a chunk including padding.
        padding (float): The padding to be applied to each side.

    Returns:
        list: A new list of segments, none of which are too long.
    """
    max_content_duration = max_total_duration - (2 * padding)
    
    if max_content_duration <= 0:
        raise ValueError("Padding is too large for the max_chunk_duration. Cannot create valid chunks.")

    processed_segments = []
    for segment in segments:
        duration = segment['end'] - segment['start']
        
        if duration <= max_content_duration:
            processed_segments.append(segment)
        else:
            print(f"-> Splitting a long segment: {segment} (content duration {duration:.2f}s > max content {max_content_duration:.2f}s)")
            current_start = segment['start']
            while current_start < segment['end']:
                new_end = current_start + max_content_duration
                final_end = min(new_end, segment['end'])
                
                new_segment = {'start': current_start, 'end': final_end}
                processed_segments.append(new_segment)
                
                current_start = final_end
                
    return processed_segments


def group_segments_with_padding_constraint(segments, max_total_duration, padding):
    """
    Groups segments into chunks, ensuring the total span *including padding*
    does not exceed max_total_duration.

    Args:
        segments (list): Pre-processed segments (already split).
        max_total_duration (float): The maximum allowed time span for a chunk with padding.
        padding (float): Seconds to add/subtract from the edges.

    Returns:
        list: A list of chunks, with padded edge segments.
    """
    if not segments:
        return []

    sorted_segments = sorted(segments, key=lambda x: x['start'])
    
    all_chunks = []
    i = 0
    while i < len(sorted_segments):
        current_chunk = [sorted_segments[i]]
        chunk_start_time = sorted_segments[i]['start']
        
        j = i + 1
        while j < len(sorted_segments):
            next_segment = sorted_segments[j]
            
            potential_padded_span = (next_segment['end'] + padding) - (chunk_start_time - padding)
            
            if potential_padded_span <= max_total_duration:
                current_chunk.append(next_segment)
                j += 1
            else:
                break
        
        first_segment = current_chunk[0]
        last_segment = current_chunk[-1]

        modified_first = {'start': max(0, first_segment['start'] - padding), 'end': first_segment['end']}
        modified_last = {'start': last_segment['start'], 'end': last_segment['end'] + padding}

        if len(current_chunk) == 1:
            modified_first['end'] = last_segment['end'] + padding
            current_chunk[0] = modified_first
        else:
            current_chunk[0] = modified_first
            current_chunk[-1] = modified_last
            
        all_chunks.append(current_chunk)
        i = j
        
    return all_chunks

